import React from 'react';
import styled from 'styled-components';
import SongList from './SongList.jsx';
import { songs1 } from './songs';
import { songs2 } from './songs';
import Wrapper from '../wrapper/wrapper.component';

const SongStyled = styled.div`

.song::before {
  content: open-quote;
}
.song::after {
  content: close-quote;
}

.song {
  font-style: italic;
}

section {
  display: flex;
  flex-direction: row;
}

p.scene {
  text-align: left;
  font-size: 16px;
  font-weight: bold;
}

 .songs {
  margin: 0 auto;
   width: 80%;
 }

 main {
  background-image: url(./lightningbolt.svg);
  background-position: center;
  fill:#a82424;
 }

@media screen and (max-width: 800px) {

.songs span {
  display: none;
}

.songs section {
  display: flex;
  flex-direction: column;
  margin-bottom: 32px;
  text-align: left;
}

p.scene {
  font-size: 16px;
  margin-bottom: 0;
  padding-bottom: 0;
}

p.song {
  font-size: 18px;
  padding: 6px 0;
  color: var(--darkred);
  font-weight: bold;;
}

}
`;



export default function Songs() {
  return (
    <Wrapper>
    <SongStyled >
      <div id="songs"></div>
        <h1>Songs</h1>
          <main>
            <h3>ACT I  </h3>
            <SongList songs={ songs1 } />

            <p>
            - There will be a 15 minute intermission before Act II  
            </p>

            <h3>ACT II  </h3> 
            <SongList songs={ songs2 } />
          </main>
      </SongStyled>
       </Wrapper>
  );
}
